/* ============================= Header ================================== */
function openNav() {
    document.getElementById("mobileNav").style.width = "50vw";
    document.body.style.backgroundColor = "rgba(0,0,0,0.4)";
}
function closeNav() {
    document.getElementById("mobileNav").style.width = "0";
    document.body.style.backgroundColor = "white";
}
